/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `billing_before_update` BEFORE UPDATE ON `billing` FOR EACH ROW BEGIN
    -- 1) subtotal
    SET NEW.subtotal_amount =
        IFNULL(NEW.fee, 0)
      + IFNULL(NEW.procedure_amount, 0)
      + IFNULL(NEW.medcert_amount, 0);

    -- 2) If discount_amount is changed manually  recalc discount_percent
    IF NEW.discount_amount IS NOT NULL
       AND NEW.discount_amount <> OLD.discount_amount THEN

        SET NEW.discount_percent =
            CASE
                WHEN NEW.subtotal_amount = 0 THEN 0
                ELSE (NEW.discount_amount / NEW.subtotal_amount) * 100
            END;

    ELSE
        -- 3) Otherwise compute discount_amount from discount_percent
        SET NEW.discount_amount =
            NEW.subtotal_amount * (IFNULL(NEW.discount_percent, 0) / 100);
    END IF;

    -- 4) total = subtotal - discount
    SET NEW.total_amount =
        NEW.subtotal_amount - IFNULL(NEW.discount_amount, 0);

    -- 5) balance = total - amount_paid
    SET NEW.balance =
        NEW.total_amount - IFNULL(NEW.amount_paid, 0);
        
        
    -- 6) payment_status logic
    IF NEW.balance IS NULL OR NEW.balance <> 0 THEN
        SET NEW.payment_status = 'UNPAID';
    ELSE
        SET NEW.payment_status = 'PAID';
    END IF;
END */;;
