/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `trg_after_insert_consultation` AFTER INSERT ON `consultation` FOR EACH ROW BEGIN
    -- 1 Declare variables and cursor first
    DECLARE v_token VARCHAR(500);
    DECLARE v_column_name VARCHAR(100);
    DECLARE done INT DEFAULT 0;
    DECLARE cur CURSOR FOR SELECT column_name, value FROM tmp_autocomplete WHERE value IS NOT NULL AND TRIM(value) <> '';
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;

    -- 2 Temporary table must be created outside the DECLARE section
    CREATE TEMPORARY TABLE IF NOT EXISTS tmp_autocomplete (
        column_name VARCHAR(100),
        value VARCHAR(500)
    );

    -- 3 Insert new consultation values
    INSERT INTO tmp_autocomplete (column_name, value)
    VALUES
        ('history', NEW.history),
        ('ear_exam', NEW.ear_exam),
        ('nose_exam', NEW.nose_exam),
        ('throat_exam', NEW.throat_exam),
        ('others_exam', NEW.others_exam),
