        ('recommendations', NEW.recommendations),
        ('maxillofacial_exam', NEW.maxillofacial_exam),
        ('head_and_neck_exam', NEW.head_and_neck_exam);

    -- 4 Process cursor loop
    OPEN cur;
    read_loop: LOOP
        FETCH cur INTO v_column_name, v_token;
        IF done THEN
            LEAVE read_loop;
        END IF;

        SET v_token = TRIM(v_token);
        IF v_token NOT IN ('AS','AD','AU') THEN
            IF EXISTS (SELECT 1 FROM autocomplete_entries WHERE column_name = v_column_name AND value = v_token) THEN
                UPDATE autocomplete_entries
                SET cnt = cnt + 1
                WHERE column_name = v_column_name AND value = v_token;
            ELSE
                INSERT INTO autocomplete_entries (column_name, value, cnt)
                VALUES (v_column_name, v_token, 1);
            END IF;
        END IF;
    END LOOP;

    CLOSE cur;

    DROP TEMPORARY TABLE IF EXISTS tmp_autocomplete;
END */;;
