    SET v_water_charge = 0.00;
ELSE
    IF v_is_initial_billing = 1 AND v_billable < 10 THEN
        -- Initial billing below 10 m  charge actual proportional rate
        SET v_water_charge = ROUND(v_billable * (v_min_rate / 10), 2);
    ELSE
