        SET v_discount_amount = ROUND((v_water_charge + v_tax_amount) * (v_discount_percent / 100), 2);
    ELSE
        SET v_discount_amount = 0;
    END IF;

    SET v_total_water_bill = ROUND(v_water_charge + v_tax_amount - v_discount_amount, 2);

